    //


    /**
     * fills the given StringBuilder with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the StringBuilder that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuilder sqlWhere, KgsequenceBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isMrnaSequenceModified()) {
                _dirtyCount ++;
                if (bean.getMrnaSequence() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("mrna_sequence IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("mrna_sequence ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isKnowngeneRefModified()) {
                _dirtyCount ++;
                if (bean.getKnowngeneRef() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("knowngene_ref IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("knowngene_ref = ?");
                }
            }
            if (bean.isSequenceIdModified()) {
                _dirtyCount ++;
                if (bean.getSequenceId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("sequence_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("sequence_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

    /**
     * fill the given prepared statement with the bean values and a search type
     * @param ps the PreparedStatement that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     * @throws DAOException
     */
    protected int fillPreparedStatement(PreparedStatement ps, KgsequenceBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isMrnaSequenceModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getMrnaSequence() + "]");
                        if (bean.getMrnaSequence() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getMrnaSequence()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getMrnaSequence() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getMrnaSequence() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getMrnaSequence() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getMrnaSequence());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getMrnaSequence() + "]");
                        if (bean.getMrnaSequence() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getMrnaSequence() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isKnowngeneRefModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getKnowngeneRef() + "]");
                if (bean.getKnowngeneRef() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getKnowngeneRef()); }
            }
            if (bean.isSequenceIdModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getSequenceId() + "]");
                if (bean.getSequenceId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getSequenceId()); }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


