    /**
     * Transforms a ResultSet iterating on the mirna table on a MirnaBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting MirnaBean bean
     * @throws DAOException
     */
    //30
    public MirnaBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        MirnaBean bean = this.createMirnaBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_FAMILY_REF:
                        ++pos;
                        bean.setFamilyRef(Manager.getInteger(rs, pos));
                        break;
                    case ID_MIRNA_SPECIES:
                        ++pos;
                        bean.setMirnaSpecies(rs.getString(pos));
                        break;
                    case ID_MIRBASE_ACCESSION:
                        ++pos;
                        bean.setMirbaseAccession(rs.getString(pos));
                        break;
                    case ID_MIRNA_SEQUENCE:
                        ++pos;
                        bean.setMirnaSequence(rs.getString(pos));
                        break;
                    case ID_MIRNA_NAME:
                        ++pos;
                        bean.setMirnaName(rs.getString(pos));
                        break;
                    case ID_MIRNA_ID:
                        ++pos;
                        bean.setMirnaId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

