    /**
     * decode a resultset in an array of TestDesignBean objects
     *
     * @param rs the resultset to decode
     * @param fieldList table of the field's associated constants
     * @param startRow the start row to be used (first row = 1, last row = -1)
     * @param numRows the number of rows to be retrieved (all rows = a negative number)
     * @return the resulting TestDesignBean table
     * @throws DAOException
     */
    //28
    public TestDesignBean[] decodeResultSet(ResultSet rs, int[] fieldList, int startRow, int numRows) throws DAOException
    {
        List<TestDesignBean> v = new ArrayList<TestDesignBean>();
        try
        {
            if (rs.absolute(startRow) && numRows!=0)
            {
                int count = 0;
                if(fieldList == null) {
                    do
                    {
                        v.add(decodeRow(rs));
                        count++;
                    } while ( (count<numRows||numRows<0) && rs.next() );
                }
                else {
                    do
                    {
                        v.add(decodeRow(rs, fieldList));
                        count++;
                    } while ( (count<numRows||numRows<0) && rs.next() );
                }
            }
            return (TestDesignBean[])v.toArray(new TestDesignBean[v.size()]);
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        finally
        {
            if (v != null) { v.clear(); v = null;}
        }
    }

    /**
     * Transforms a ResultSet iterating on the test_design on a TestDesignBean bean.
     *
     * @param rs the ResultSet to be transformed
     * @return bean resulting TestDesignBean bean
     * @throws DAOException
     */
    //29
    public TestDesignBean decodeRow(ResultSet rs) throws DAOException
    {
        TestDesignBean bean = this.createTestDesignBean();
        try
        {
            bean.setTestDesign(rs.getString(1));
            bean.setTestId(Manager.getInteger(rs, 2));
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

    /**
     * Transforms a ResultSet iterating on the test_design table on a TestDesignBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TestDesignBean bean
     * @throws DAOException
     */
    //30
    public TestDesignBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TestDesignBean bean = this.createTestDesignBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TEST_DESIGN:
                        ++pos;
                        bean.setTestDesign(rs.getString(pos));
                        break;
                    case ID_TEST_ID:
                        ++pos;
                        bean.setTestId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

